// filepath: /home/vboxuser/apps-dev/myweather-app/src/components/WeatherGraph/WeatherGraph.js
import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './WeatherGraph.css'; // Import the CSS file

const WeatherGraph = ({ data }) => {

    const filteredData = data.filter((entry) => {
        const time = entry.dt_txt.split(" ")[1]; // Extract time (HH:MM:SS)
        const hour = parseInt(time.split(":")[0], 10); // Extract the hour
        return hour >= 0 && hour <= 12; // Include only times between 12 AM and 12 PM
      });

  const chartData = {
    labels: filteredData.map((entry) => entry.dt_txt.split(" ")[1]), // Extract time (HH:MM)
    datasets: [
      {
        label: 'Temperature (째C)',
        data: filteredData.map((entry) => entry.main.temp),
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderWidth: 2,
        tension: 0.4,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        display: true,
        position: 'top',
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Temperature (째C)',
        },
        beginAtZero: false,
      },
    },
  };

  return (
    <div className="weather-graph-container">
        <h3 className="weather-graph-title">12 AM to 12 PM Temperature Forecast</h3>
        <Line data={chartData} options={options} />
    </div>
);
};

export default WeatherGraph;
