import React, { useState } from 'react';
import './App.css';
import './quote-service/quote-service.css'; 
import Search from './src/components/search/search';
import Forecast from './src/components/forecast/forecast';
import CurrentWeather from './src/components/current-weather/current-weather';
import fetchQuote from './quote-service/quote-service';
import QuoteDisplay from './quote-service/QuoteDisplay';
import { WEATHER_API_URL, WEATHER_API_KEY } from './api';

function App() {
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [quote, setQuote] = useState(null); 

  const handleOnSearchChange = (searchData) => {
    const [lat, lon] = searchData.value.split(" ");

    const CurrentWeatherFetch = fetch(`${WEATHER_API_URL}/weather?lat=${lat}&lon=${lon}&units=metric&appid=${WEATHER_API_KEY}&&units=metric`)
    const ForecastWeatherFetch = fetch(`${WEATHER_API_URL}/forecast?lat=${lat}&lon=${lon}&units=metric&appid=${WEATHER_API_KEY}&&units=metric`)

    Promise.all([CurrentWeatherFetch, ForecastWeatherFetch])
      .then(async (response) => {
        const weatherResponse = await response[0].json();
        const forecastResponse = await response[1].json();

        setCurrentWeather({ city: searchData.label, ...weatherResponse });
        setForecast({ city: searchData.label, ...forecastResponse });

        const quote = await fetchQuote(searchData.label);
        setQuote(quote);
      })
      .catch(err => console.log(err));
  }

  console.log(currentWeather);
  console.log(forecast);
  console.log(quote);

  return (
    <div className="container">
      <Search onSearchChange={handleOnSearchChange} />
      {currentWeather && <CurrentWeather data={currentWeather} />}
      {quote && <QuoteDisplay quote={quote} />}
      {forecast && <Forecast data={forecast}/>}
    </div>
  );
}

export default App;
